mapper.update:
UPDATE TBL_BOARD_REPLY
SET BOARD_REPLY_CONTENT = #{boardReplyContent} , BOARD_REPLY_UPDATE_DATE = SYSDATE
WHERE BOARD_REPLY_NUMBER = #{boardReplyNumber}

mapper.select:
SELECT BOARD_REPLY_NUMBER, BOARD_REPLY_CONTENT, BOARD_REPLY_REGISTER_DATE, BOARD_REPLY_UPDATE_DATE, BOARD_NUMBER, USER_NUMBER, USER_NICKNAME FROM ( SELECT ROWNUM RN, BOARD_REPLY_NUMBER, BOARD_REPLY_CONTENT, BOARD_REPLY_REGISTER_DATE, BOARD_REPLY_UPDATE_DATE, BOARD_NUMBER, USER_NUMBER, USER_NICKNAME FROM( SELECT BOARD_REPLY_NUMBER, BOARD_REPLY_CONTENT, BOARD_REPLY_REGISTER_DATE, BOARD_REPLY_UPDATE_DATE, BOARD_NUMBER, U.USER_NUMBER, U.USER_NICKNAME FROM TBL_USER U JOIN TBL_BOARD_REPLY R ON BOARD_NUMBER = #{boardNumber} AND U.USER_NUMBER = R.USER_NUMBER ORDER BY BOARD_REPLY_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > ( #{criteria.page} - 1 ) * #{criteria.amount}

SELECT COUNT(BOARD_REPLY_NUMBER)
FROM TBL_BOARD_REPLY
WHERE BOARD_NUMBER = #{boardNumber}
