mapper.select:
SELECT CARE_SHOP_NUMBER, CARE_SHOP_NAME, CARE_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT ROWNUM AS RNUM, HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT H.HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, U.USER_NUMBER, MEMBERSHIP_NUMBER FROM TBL_USER U JOIN TBL_HAIR_SHOP H ON U.USER_NUMBER = H.USER_NUMBER LEFT JOIN( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER FROM( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER, RANK() OVER(PARTITION BY HAIR_SHOP_NUMBER ORDER BY HAIR_SHOP_FILE_NUMBER) RK FROM TBL_HAIR_SHOP_FILE ) WHERE RK = 1 ) J ON H.HAIR_SHOP_NUMBER = J.HAIR_SHOP_NUMBER ORDER BY H.HAIR_SHOP_NUMBER DESC ) T1
WHERE ROWNUM <= #{page} * #{amount}
) T2 WHERE RNUM > (#{page} - 1) * #{amount}

mapper.update:
UPDATE TBL_HAIR_SHOP
SET HAIR_SHOP_ADDRESS = #{hairShopAddress}, HAIR_SHOP_PHONE_NUMBER = #{hairShopPhoneNumber},
    HAIR_SHOP_OPEN_TIME = #{hairShopOpenTime}, HAIR_SHOP_NAME = #{hairShopName},
    HAIR_SHOP_LINK = #{hairShopLink}, HAIR_SHOP_CONTENT = #{hairShopContent}
WHERE HAIR_SHOP_NUMBER = #{hairShopNumber}

mapper.select:
SELECT HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT ROWNUM AS RNUM, HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT H.HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, U.USER_NUMBER, MEMBERSHIP_NUMBER FROM TBL_USER U JOIN TBL_HAIR_SHOP H ON U.USER_NUMBER = H.USER_NUMBER LEFT JOIN ( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER FROM ( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER, RANK() OVER (PARTITION BY HAIR_SHOP_NUMBER ORDER BY HAIR_SHOP_FILE_NUMBER) RK FROM TBL_HAIR_SHOP_FILE ) WHERE RK = 1 ) J ON H.HAIR_SHOP_NUMBER = J.HAIR_SHOP_NUMBER WHERE
HAIR_SHOP_NAME LIKE '%' || #{search.keyword} || '%'
HAIR_SHOP_ADDRESS LIKE '%' || #{search.keyword} || '%'
ORDER BY H.HAIR_SHOP_NUMBER DESC ) T1
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) T2 WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
