mapper.select:
SELECT HAIR_NUMBER, HAIR_NAME, HAIR_FILE_NAME, HAIR_FILE_UUID, HAIR_FILE_UPLOAD_PATH FROM ( SELECT ROWNUM AS RNUM , HAIR_NUMBER, HAIR_NAME, HAIR_FILE_NAME, HAIR_FILE_UUID, HAIR_FILE_UPLOAD_PATH FROM ( SELECT th.HAIR_NUMBER, HAIR_NAME, thf.HAIR_FILE_NAME, thf.HAIR_FILE_UUID, thf.HAIR_FILE_UPLOAD_PATH FROM TBL_HAIR th LEFT JOIN ( SELECT HAIR_FILE_NUMBER , HAIR_FILE_NAME , HAIR_FILE_UPLOAD_PATH , HAIR_FILE_UUID , HAIR_NUMBER FROM ( SELECT HAIR_FILE_NUMBER , HAIR_FILE_NAME , HAIR_FILE_UPLOAD_PATH , HAIR_FILE_UUID , HAIR_NUMBER, RANK() OVER(PARTITION BY HAIR_NUMBER ORDER BY HAIR_FILE_NUMBER) RK FROM TBL_HAIR_FILE) WHERE RK = 1) thf ON th.HAIR_NUMBER = thf.HAIR_NUMBER ORDER BY HAIR_NUMBER DESC)
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1 ) * #{amount}

SELECT HAIR_NUMBER, HAIR_NAME, HAIR_CONTENT, HAIR_GENDER, SHAPE_NUMBER, LENGTH_NUMBER, HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID
FROM (
    SELECT ROWNUM AS RNUM, HAIR_NUMBER, HAIR_NAME, HAIR_CONTENT, HAIR_GENDER, SHAPE_NUMBER, LENGTH_NUMBER, HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID
    FROM (
        SELECT H.HAIR_NUMBER, HAIR_NAME, HAIR_CONTENT, HAIR_GENDER, SHAPE_NUMBER, LENGTH_NUMBER, HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID
        FROM TBL_HAIR H
        LEFT JOIN (
            SELECT HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID, HAIR_NUMBER
            FROM (
                SELECT HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID, HAIR_NUMBER, RANK() over (PARTITION BY HAIR_NUMBER ORDER BY HAIR_FILE_NUMBER) RK
                FROM TBL_HAIR_FILE
            )
            WHERE RK = 1
        ) F
        ON H.HAIR_NUMBER = F.HAIR_NUMBER


                AND HAIR_GENDER LIKE '%' || #{searchHairVo.hairGender} || '%'


                AND SHAPE_NUMBER = #{searchHairVo.shapeNumber}


                AND LENGTH_NUMBER = #{searchHairVo.lengthNumber}


                AND HAIR_NAME LIKE '%' || #{searchHairVo.keyword} || '%'


    )
    WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}

)
WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT COUNT(H.HAIR_NUMBER) FROM TBL_HAIR H LEFT JOIN ( SELECT HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID, HAIR_NUMBER FROM ( SELECT HAIR_FILE_NUMBER, HAIR_FILE_NAME, HAIR_FILE_UPLOAD_PATH, HAIR_FILE_UUID, HAIR_NUMBER, RANK() over (PARTITION BY HAIR_NUMBER ORDER BY HAIR_FILE_NUMBER) RK FROM TBL_HAIR_FILE ) WHERE RK = 1 ) F ON H.HAIR_NUMBER = F.HAIR_NUMBER
AND HAIR_GENDER LIKE '%' || #{hairGender} || '%'
AND SHAPE_NUMBER = #{shapeNumber}
AND LENGTH_NUMBER = #{lengthNumber}
AND HAIR_NAME LIKE '%' || #{keyword} || '%'
