mapper.select:
SELECT HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT ROWNUM AS RNUM, HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, USER_NUMBER, MEMBERSHIP_NUMBER FROM ( SELECT H.HAIR_SHOP_NUMBER, HAIR_SHOP_NAME, HAIR_SHOP_ADDRESS, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, U.USER_NUMBER, MEMBERSHIP_NUMBER FROM TBL_USER U JOIN TBL_HAIR_SHOP H ON U.USER_NUMBER = H.USER_NUMBER LEFT JOIN( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER FROM( SELECT HAIR_SHOP_FILE_NUMBER, HAIR_SHOP_FILE_UPLOAD_PATH, HAIR_SHOP_FILE_UUID, HAIR_SHOP_FILE_NAME, HAIR_SHOP_NUMBER, RANK() OVER(PARTITION BY HAIR_SHOP_NUMBER ORDER BY HAIR_SHOP_FILE_NUMBER) RK FROM TBL_HAIR_SHOP_FILE ) WHERE RK = 1 ) J ON H.HAIR_SHOP_NUMBER = J.HAIR_SHOP_NUMBER ORDER BY H.HAIR_SHOP_NUMBER DESC ) T1
WHERE ROWNUM <= #{page} * #{amount}
) T2 WHERE RNUM > (#{page} - 1) * #{amount}
