mapper.update:
UPDATE TBL_STORE_REPLY
SET STORE_REPLY_CONTENT = #{storeReplyContent} , STORE_REPLY_UPDATE_DATE = SYSDATE
WHERE STORE_REPLY_NUMBER = #{storeReplyNumber}

mapper.select:
SELECT STORE_REPLY_NUMBER, STORE_REPLY_CONTENT, STORE_REPLY_REGISTER_DATE, STORE_REPLY_UPDATE_DATE, STORE_NUMBER, USER_NUMBER, USER_NICKNAME, STORE_SCORE FROM ( SELECT ROWNUM RN, STORE_REPLY_NUMBER, STORE_REPLY_CONTENT, STORE_REPLY_REGISTER_DATE, STORE_REPLY_UPDATE_DATE, STORE_NUMBER, USER_NUMBER, USER_NICKNAME, STORE_SCORE FROM( SELECT STORE_REPLY_NUMBER, STORE_REPLY_CONTENT, STORE_REPLY_REGISTER_DATE, STORE_REPLY_UPDATE_DATE, STORE_NUMBER, U.USER_NUMBER, U.USER_NICKNAME, STORE_SCORE FROM TBL_USER U JOIN TBL_STORE_REPLY R ON STORE_NUMBER = #{storeNumber} AND U.USER_NUMBER = R.USER_NUMBER ORDER BY STORE_REPLY_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > ( #{criteria.page} - 1 ) * #{criteria.amount}

SELECT COUNT(STORE_REPLY_NUMBER)
FROM TBL_STORE_REPLY
WHERE STORE_NUMBER = #{storeNumber}

SELECT COALESCE(ROUND(AVG(STORE_SCORE), 1), 0) AS scoreAvg
FROM TBL_STORE_REPLY
WHERE STORE_NUMBER = #{storeNumber}
